mapper.select:
 select

from task_plan
where 1=1

    and jiacn = #{jiacn,jdbcType=VARCHAR}


    and type = #{type,jdbcType=INTEGER}


    and status = #{status,jdbcType=INTEGER}


    and start_time >= #{startTimeStart,jdbcType=INTEGER}


    and #{startTimeEnd,jdbcType=INTEGER} > start_time


    and end_time >= #{endTimeStart,jdbcType=INTEGER}


    and #{endTimeEnd,jdbcType=INTEGER} > end_time


    and (status = 0 or UNIX_TIMESTAMP() > end_time)
